```go
func Min(a, b int) int {
	if a < b {
		return a
	}
	return b
}

func longestPalindrome(s string) string {
	ss := make([]byte, len(s)*2+1)
	ss[0] = '#'
	si := 1
	for i := 0; i < len(s); i++ {
		ss[si] = s[i]
		si++
		ss[si] = '#'
		si++
	}

	// Manacher's algorithm
	p := make([]int, len(ss))
	mid, maxRight := 0, 0
	maxPos := 0
	for i := 0; i < len(ss); i++ {
		if i < maxRight {
			p[i] = Min(p[2*mid-i], maxRight-i)
		} else {
			p[i] = 1
		}
		for i+p[i] < len(ss) && i-p[i] > -1 && ss[i+p[i]] == ss[i-p[i]] {
			p[i]++
		}
		if i+p[i] > maxRight {
			mid = i
			maxRight = i + p[i]
		}
	}
	for i := 0; i < len(p); i++ {
		if p[i] > p[maxPos] {
			maxPos = i
		}
	}
	return s[(maxPos-p[maxPos]+1)/2 : (maxPos+p[maxPos]-1)/2]
}
```







